mapper.insert:
insert into message (id, userId, caseId, 
  content, submitTime, replyConten, 
  replyTime, state, departionId,adminid
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{caseid,jdbcType=INTEGER}, 
  #{content,jdbcType=VARCHAR}, #{submittime,jdbcType=DATE}, #{replyconten,jdbcType=VARCHAR}, 
  #{replytime,jdbcType=DATE}, #{state,jdbcType=CHAR}, #{departionid,jdbcType=BIGINT}, #{adminid,jdbcType=INTEGER}
  )

mapper.select:
SELECT message.*,`user`.userName,`case`.caseCode,`case`.caseName
from message LEFT JOIN `user` on `user`.Id=message.userId
LEFT JOIN `case` on `case`.id=message.caseId


        and message.departionId=#{departmentid}


        and state=#{state}



    limit #{start},#{count}

SELECT count(*)
from message LEFT JOIN `user` on `user`.Id=message.userId
LEFT JOIN `case` on `case`.id=message.caseId


        and message.departionid=#{departmentid}


        and state=#{state}
